import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { createConsultation, getSingleUser } from "../services/userServices";
import { NotificationContainer, NotificationManager } from 'react-notifications';
import 'react-notifications/lib/notifications.css';

import {
  inputValidation,
} from "../services/validationService";

const AddConsultation = () => {
  const navigate = useNavigate();
  const { d } = useParams();

  const [formData, setFormData] = useState({
    patientName: '',
    date: '',
    consultationType: '',
    medicalCondition: '',
    healthcareProvider: '',
    notes: ''
  });

  useEffect(() => {
    getSingleUser(userId)
      .then(response => {
        if (response) {
          console.log(response);
        } else {
          console.log("No data");
        }
      })
      .catch(err => {
        console.log(err);
      });
  }, [userId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({ ...prevState, [name]: value }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const { patientName, date, healthcareProvider, consultationType, medicalCondition, notes } = formData;

    if (!inputValidation(patientName) || !inputValidation(date) || !inputValidation(healthcareProvider) ||
        !inputValidation(consultationType) || !inputValidation(medicalCondition) || !inputValidation(notes)) {
      NotificationManager.error("All values must be more than four characters", "Invalid Input");
      return;
    }

    const userDetails = JSON.parse(localStorage.getItem("user"));

    const data = {
      ...formData,
      officer: userDetails.id
    };

    try {
      const resp = await createConsultation(data);
      if (resp.status === 200) {
        NotificationManager.success("Consultation created successfully");
        navigate("/");
      } else {
        NotificationManager.error("Could not save");
      }
    } catch (error) {
      NotificationManager.error("An error occurred while saving");
      console.log(error);
    }

    setFormData({
      patientName: '',
      date: '',
      consultationType: '',
      medicalCondition: '',
      healthcareProvider: '',
      notes: ''
    });
  };

  return (
    <>
      <div className="w-full max-w-sm md:mx-[450px] mt-20 p-4 bg-white border border-gray-200 rounded-lg shadow sm:p-6 md:p-8 dark:bg-gray-800 dark:border-gray-700">
        <div className="space-y-6">
          <h5 className="text-xl font-medium text-gray-900 dark:text-white">Book a Consultation</h5>
          <div>
            <input
              name="patientName"
              value={formData.patientName}
              onChange={handleChange}
              placeholder="Patient Name"
              className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white"
              required
            />
          </div>
          <div>
            <input
              name="consultationType"
              value={formData.consultationType}
              onChange={handleChange}
              placeholder="Type"
              className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white"
              required
            />
          </div>
          <div>
            <input
              name="medicalCondition"
              value={formData.medicalCondition}
              onChange={handleChange}
              placeholder="Medical Condition"
              className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white"
              required
            />
          </div>
          <div>
            <input
              name="healthcareProvider"
              value={formData.healthcareProvider}
              onChange={handleChange}
              placeholder="Healthcare Provider"
              className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white"
              required
            />
          </div>
          <div>
            <textarea
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              placeholder="Notes"
              className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white"
              required
            />
          </div>
          <div>
            <input
              type="date"
              name="date"
              value={formData.date}
              onChange={handleChange}
              placeholder="Date"
              className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white"
              required
            />
          </div>
          <button
            onClick={handleSubmit}
            className="w-full text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
          >
            Book Consultation
          </button>
        </div>
        <NotificationContainer />
      </div>
    </>
  );
};

export default AddConsultation;
