import React, { useState } from 'react';
import axios from 'axios';

const ConsultationForm = () => {
  const [patient, setPatient] = useState('');
  const [date, setDate] = useState('');
  const [type, setType] = useState('');
  const [medicalCondition, setMedicalCondition] = useState('');
  const [notes, setNotes] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    const token = localStorage.getItem('token');
    await axios.post('/api/consultations', {
      patient, date, type, medicalCondition, notes
    }, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input value={patient} onChange={(e) => setPatient(e.target.value)} placeholder="Patient" />
      <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
      <input value={type} onChange={(e) => setType(e.target.value)} placeholder="Type" />
      <input value={medicalCondition} onChange={(e) => setMedicalCondition(e.target.value)} placeholder="Medical Condition" />
      <textarea value={notes} onChange={(e) => setNotes(e.target.value)} placeholder="Notes" />
      <button type="submit">Book Consultation</button>
    </form>
  );
};








 // Ensure axios is configured as mentioned before

const FilterConsultations = () => {
  const [date, setDate] = useState('');
  const [patientName, setPatientName] = useState('');
  const [healthcareProvider, setHealthcareProvider] = useState('');
  const [consultationType, setConsultationType] = useState('');
  const [medicalCondition, setMedicalCondition] = useState('');
  const [consultations, setConsultations] = useState([]);

  const handleFilter = async (e) => {
    e.preventDefault();
    const params = {
      date,
      patientName,
      healthcareProvider,
      consultationType,
      medicalCondition,
    };
    
    try {
      const response = await axios.get('/api/consultations', { params });
      setConsultations(response.data);
    } catch (error) {
      console.error('Error fetching consultations:', error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <form onSubmit={handleFilter} className="space-y-4">
        <div className="flex flex-col">
          <label className="mb-1 text-gray-700">Date:</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="p-2 border border-gray-300 rounded"
          />
        </div>
        <div className="flex flex-col">
          <label className="mb-1 text-gray-700">Patient Name:</label>
          <input
            type="text"
            value={patientName}
            onChange={(e) => setPatientName(e.target.value)}
            className="p-2 border border-gray-300 rounded"
          />
        </div>
        <div className="flex flex-col">
          <label className="mb-1 text-gray-700">Healthcare Provider:</label>
          <input
            type="text"
            value={healthcareProvider}
            onChange={(e) => setHealthcareProvider(e.target.value)}
            className="p-2 border border-gray-300 rounded"
          />
        </div>
        <div className="flex flex-col">
          <label className="mb-1 text-gray-700">Consultation Type:</label>
          <input
            type="text"
            value={consultationType}
            onChange={(e) => setConsultationType(e.target.value)}
            className="p-2 border border-gray-300 rounded"
          />
        </div>
        <div className="flex flex-col">
          <label className="mb-1 text-gray-700">Medical Condition:</label>
          <input
            type="text"
            value={medicalCondition}
            onChange={(e) => setMedicalCondition(e.target.value)}
            className="p-2 border border-gray-300 rounded"
          />
        </div>
        <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-700">
          Filter
        </button>
      </form>

      <div className="mt-8">
        <h2 className="text-xl font-bold mb-4">Consultations</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border border-gray-300">
            <thead>
              <tr>
                <th className="py-2 px-4 border-b">Date</th>
                <th className="py-2 px-4 border-b">Patient</th>
                <th className="py-2 px-4 border-b">Healthcare Provider</th>
                <th className="py-2 px-4 border-b">Consultation Type</th>
                <th className="py-2 px-4 border-b">Medical Condition</th>
              </tr>
            </thead>
            <tbody>
              {consultations.map((consultation) => (
                <tr key={consultation._id}>
                  <td className="py-2 px-4 border-b">{new Date(consultation.date).toLocaleDateString()}</td>
                  <td className="py-2 px-4 border-b">
                    {consultation.patient.firstName} {consultation.patient.lastName}
                  </td>
                  <td className="py-2 px-4 border-b">{consultation.officer.username}</td>
                  <td className="py-2 px-4 border-b">{consultation.consultationType}</td>
                  <td className="py-2 px-4 border-b">{consultation.medicalCondition}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default FilterConsultations;
