import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {createConsultation} from "../services/userServices"

import { NotificationContainer, NotificationManager } from 'react-notifications';
import 'react-notifications/lib/notifications.css';



import {
    emailValidation,
   inputValidation ,
   textAreaValidation,
} from "../services/validationService"

const AddConsultation = () => {
  const navigate = useNavigate()
  const [patientName, setPatientName] = useState('');
  const [date, setDate] = useState('');
  const [consultationType, setconsultationType] = useState('');
  const [medicalCondition, setMedicalCondition] = useState(''); 
  const [healthcareProvider, setHealthcareProvider] = useState('')
  const [notes, setNotes] = useState(''); 

  React.useEffect(() => {
    const checkUserLogin = localStorage.getItem("user")
    const user = JSON.parse(checkUserLogin)
    const data={officerId: user.id}
    const resp = getSingleConsulation(data)
    .then((datas)=>{
      if (datas){
        setConsultations(datas,"user")

      }else{
        console.log("no data")
      }
    }).catch(err=>{
      console.log(err)
    })
  }, []);


  const handleSubmit = async (event) => {
    event.preventDefault();
    const getUserDetails = JSON.parse(localStorage.getItem("user"))
    if (!inputValidation(patientName)){
      NotificationManager.error("All Value Must Be More Than Four Character","Invalid Input" );
      return
    }
    if (!inputValidation(date)){
      NotificationManager.error("All Value Must Be More Than Four Character","Invalid Input" );
      return
    }
    if (!inputValidation(healthcareProvider)){
      NotificationManager.error("All Value Must Be More Than Four Character","Invalid Input" );
      return
    }
    if (!inputValidation(consultationType)){
      NotificationManager.error("All Value Must Be More Than Four Character","Invalid Input" );
      return
    }
    if (!inputValidation(medicalCondition)){
      NotificationManager.error("All Value Must Be More Than Four Character","Invalid Input" );
      return
    }
    if (!inputValidation(notes)){
      NotificationManager.error("All Value Must Be More Than Four Character","Invalid Input" );
      return
    }

    
    const data ={
      patientName, date,healthcareProvider, consultationType, medicalCondition, notes, officer : getUserDetails.id
    }
    console.log(data)

    const resp = await createConsultation(data)
      if(resp.status===200){
        navigate("/")
      }else{
        NotificationManager.error("Could not save" );
      }

   
    
    setDate("")
    setMedicalCondition("")
    setNotes("")
    setPatientName("")
    setconsultationType("")
    
  };

  const handlepatientName = (e)=>{
    setPatientName(e.target.value)

  }
  const handleDate = (e)=>{
    setDate(e.target.value)
    
  }
  const handleHealthcareProvider = (e)=>{
    setHealthcareProvider(e.target.value)
    
  }
  const handleConsultationType = (e)=>{
    setconsultationType(e.target.value)
    
  }
  const handlemedicalCondition = (e)=>{
    setMedicalCondition(e.target.value)
    
  }
  const handleNote = (e)=>{
    setNotes(e.target.value)
    
  }

  return (
  <>
    <div className="w-full max-w-sm md:mx-[450px] mt-20 p-4 bg-white border border-gray-200 rounded-lg shadow sm:p-6 md:p-8 dark:bg-gray-800 dark:border-gray-700">
    <div className="space-y-6">
        <h5 className="text-xl font-medium text-gray-900 dark:text-white">SignUp in to our platform</h5>
        <div>
            <input placeholder="Patient Name" value={patientName}  onChange={handlepatientName}  className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white" required />
        </div>
        <div>
            <input placeholder="Type" value={consultationType}  onChange={handleConsultationType}  name="type" className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white" required />
        </div>
        <div>
            <input placeholder="Medical Condition" value={medicalCondition} onChange={handlemedicalCondition} className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white"  required />
        </div>
        <div>
            <input placeholder="Healthcare Provider" value={healthcareProvider} onChange={handleHealthcareProvider} className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white"  required />
        </div>
        <div>
            <textarea  placeholder="Notes" value={notes} onChange={handleNote}    className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white" required />
        </div>
        <div>
            <input type="date" placeholder="Date" value={date} onChange={handleDate}  className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white" required />
        </div>
        
        <button onClick={handleSubmit}  className="w-full text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"> Book Consultation</button>
       
    </div>
    <NotificationContainer/>
</div>
    </>
  );
};

export default AddConsultation;
